import React, { useState } from "react";

function App() {
  const [image, setImage] = useState(null);
  const [patternURL, setPatternURL] = useState("");
  const [cols, setCols] = useState(50);
  const [stitchWidth, setStitchWidth] = useState(1.0);
  const [stitchHeight, setStitchHeight] = useState(1.0);

  const handleUpload = async () => {
    if (!image) return;
    const formData = new FormData();
    formData.append("image", image);
    formData.append("cols", cols);
    formData.append("stitch_width", stitchWidth);
    formData.append("stitch_height", stitchHeight);

    const response = await fetch("http://127.0.0.1:5001/api/upload", {
      method: "POST",
      body: formData,
    });

    const data = await response.json();
    setPatternURL(`http://127.0.0.1:5001${data.pattern_image}`);
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h1>iCrochet Pattern Generator</h1>
      <input type="file" onChange={(e) => setImage(e.target.files[0])} />
      <div>
        <label>Columns: </label>
        <input type="number" value={cols} onChange={(e) => setCols(e.target.value)} />
      </div>
      <div>
        <label>Stitch Width (cm): </label>
        <input type="number" value={stitchWidth} onChange={(e) => setStitchWidth(e.target.value)} />
      </div>
      <div>
        <label>Stitch Height (cm): </label>
        <input type="number" value={stitchHeight} onChange={(e) => setStitchHeight(e.target.value)} />
      </div>
      <button onClick={handleUpload}>Generate Pattern</button>

      {patternURL && <img src={patternURL} alt="Stitch Pattern" style={{ marginTop: "1rem" }} />}
    </div>
  );
}

export default App;
