import React, { useState } from "react";

export default function App() {
  const [image, setImage] = useState(null);
  const [patternURL, setPatternURL] = useState("");
  const [cols, setCols] = useState(50);
  const [stitchWidth, setStitchWidth] = useState(1.0);
  const [stitchHeight, setStitchHeight] = useState(1.0);

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!image) return;

    const formData = new FormData();
    formData.append("image", image);
    formData.append("cols", cols);
    formData.append("stitch_width", stitchWidth);
    formData.append("stitch_height", stitchHeight);

    const res = await fetch("http://localhost:5001/api/upload", {
      method: "POST",
      body: formData,
    });

    const data = await res.json();
    setPatternURL(data.pattern_image || "");
  };

  return (
    <div
      className="min-h-screen bg-black text-pink-400 flex flex-col items-center p-8 space-y-6"
      style={{ fontFamily: "'Press Start 2P', cursive" }}
    >
      {/* Header */}
      <h1 className="text-2xl mb-4 text-yellow-400 pixel-border">
        ðŸŽ® iCrochet Arcade ðŸŽ®
      </h1>

      {/* Upload Form */}
      <form
        onSubmit={handleUpload}
        className="flex flex-col items-center space-y-4"
      >
        {/* File Input */}
        <input
          type="file"
          onChange={(e) => setImage(e.target.files[0])}
          className="text-teal-300 bg-black border-4 border-pink-400 p-2"
        />

        {/* Options */}
        <label className="text-yellow-300">
          Columns:
          <input
            type="number"
            value={cols}
            onChange={(e) => setCols(e.target.value)}
            className="ml-2 bg-black border-4 border-teal-400 text-pink-300 w-20 p-1"
          />
        </label>

        <label className="text-yellow-300">
          Stitch Width (cm):
          <input
            type="number"
            value={stitchWidth}
            step="0.1"
            onChange={(e) => setStitchWidth(e.target.value)}
            className="ml-2 bg-black border-4 border-teal-400 text-pink-300 w-20 p-1"
          />
        </label>

        <label className="text-yellow-300">
          Stitch Height (cm):
          <input
            type="number"
            value={stitchHeight}
            step="0.1"
            onChange={(e) => setStitchHeight(e.target.value)}
            className="ml-2 bg-black border-4 border-teal-400 text-pink-300 w-20 p-1"
          />
        </label>

        {/* Upload Button */}
        <button type="submit" className="arcade-button">
          UPLOAD
        </button>
      </form>

      {/* Output */}
      {patternURL && (
        <div className="mt-8 border-8 border-teal-400 p-4 bg-black">
          <img
            src={`http://localhost:5001${patternURL}`}
            alt="Generated Pattern"
            style={{
              imageRendering: "pixelated",
              width: "80%",
              maxWidth: "600px",
              height: "auto",
            }}
          />
        </div>
      )}

      {/* Optional CRT overlay */}
      <div className="scanlines"></div>
    </div>
  );
}
