import React, { useState } from "react";

export default function App() {
  const [file, setFile] = useState(null);
  const [outputUrl, setOutputUrl] = useState(null);

  const handleUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("file", file);

    const res = await fetch("http://localhost:5001/api/upload", {
      method: "POST",
      body: formData,
    });

    const data = await res.json();
    setOutputUrl(data.output_url);
  };

  return (
    <div className="min-h-screen bg-black text-pink-400 flex flex-col items-center p-8"
         style={{ fontFamily: "'Press Start 2P', cursive" }}>

      {/* Header */}
      <h1 className="text-2xl mb-8 text-yellow-400 pixel-border">
        iCrochet
      </h1>

      {/* Upload Form */}
      <form onSubmit={handleUpload} className="flex flex-col items-center space-y-4">
        <input
          type="file"
          onChange={(e) => setFile(e.target.files[0])}
          className="text-teal-300 bg-black border-4 border-pink-400 p-2"
        />
        <button
          type="submit"
          className="bg-pink-500 text-black border-4 border-yellow-400 px-6 py-3
                     hover:bg-pink-400 hover:scale-110 transition-transform duration-100">
          UPLOAD
        </button>
      </form>

      {/* Output */}
      {outputUrl && (
        <div className="mt-8 border-8 border-teal-400 p-4 bg-black">
          <img
            src={outputUrl}
            alt="Generated Pattern"
            style={{
              imageRendering: "pixelated",
              width: "80%",
              maxWidth: "600px",
              height: "auto",
            }}
          />
        </div>
      )}
    </div>
  );
}
